mapper.update:
update Reply_3jo
set reparent=#{replyno}
where replyno=#{replyno}

mapper.insert:

	select ifnull(max(reorder),0)+1 from Reply_3jo where reparent=#{reparent} and tradeno=#{tradeno}

insert into Reply_3jo(tradeno,user_id,reply_content,reply_writedate,reorder,reparent,dept)
values(#{tradeno},#{user_id},#{reply_content},sysdate(),#{reorder},#{reparent},1)

