	set category=#{category}
		,title=#{title}
		,content=#{content}
		,map_x=#{map_x}
		,map_y=#{map_y}
where tradeno=#{tradeno}

mapper.select:
select real_name
from File_3jo
where tradeno=#{no}

mapper.update:
