mapper.select:
select user_id, pwd
  from User_3jo
 where user_id = #{user_id}
 and pwd = #{pwd}

select user_id
from User_3jo
where user_name=#{user_name}
and birthday=#{birthday}
and phoneno=#{phoneno}

select email
from User_3jo
where email=#{email}

mapper.update:
update User_3jo
set pwd=#{pwd}
where email=#{email}

update User_3jo
set
    addr1=#{addr1},
    addr2=#{addr2}
where user_id=#{user_id}

mapper.select:
select addr1
from User_3jo
where user_id=#{user_id}
