mapper.select:
select user_id, pwd
  from User_3jo
 where user_id = #{user_id}
 and pwd = #{pwd}

select user_id
from User_3jo
where user_name=#{user_name}
and birthday=#{birthday}
and phoneno=#{phoneno}

mapper.update:
update frome User_3jo
set pwd=#{pwd}
where user_id=#{user_id}
and   hint=#{hint}
